import styled from 'styled-components';
import { Link } from 'react-router-dom';

export const SideBarMenuTitle = styled.span``;

export const SideBarMenuName = styled.span`
  position: relative;
  color: transparent;
  background-image: linear-gradient(
    90deg,
    ${(props) => props.theme.Orange} 0%,
    ${(props) => props.theme.Orange} 50%,
    ${(props) => props.theme.OffWhite} 50%,
    ${(props) => props.theme.OffWhite} 100%
  );
  background-repeat: repeat;
  background-size: 200%;
  background-position: 100% 0;
  -webkit-background-clip: text;
  background-clip: text;
  transition: background-position 300ms;
`;

export const SideBarMenuButton = styled(Link)`
  display: flex;
  justify-content: left;
  padding-left: 40px;
  align-items: center;
  position: relative;
  width: 100%;
  height: 4rem;
  font-size: 2rem;
  cursor: pointer;
  text-decoration: none;
  font-weight: bold;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: rgba(255, 255, 255, 1);
    transform-origin: 100% 0;
    transform: scale3d(0, 1, 1);
    transition: transform 300ms;
  }

  &:hover ${SideBarMenuName} {
    background-position: 0 0;
  }

  &:hover::before {
    transform-origin: 0 0;
    transform: scale3d(1, 1, 1);
  }
`;

export const DropdownMenuLink = styled(Link)`
  font-size: 1rem;
  display: flex;
  flex-direction: column;
  align-items: center;
  color: ${(props) => props.theme.OffWhite};
  text-decoration: none;
  height: 100%;
  width: 100%;
  padding: 0.8rem 1rem;
  transition: all 0.5s;

  &::after {
    content: '${(props) => props.icon}';
    font-weight: normal;
    position: absolute;
    left: 70%;
    top: 27%;
    right: 15%;
    bottom: 0;
    opacity: 0;
  }

  &:hover {
    background: ${(props) => props.theme.OffWhite};
    padding: 0.7rem 2rem 0.7rem 0.7rem;

    color: ${(props) => props.theme.Orange};
    font-weight: bold;
    border-radius: inherit;

    &::after {
      opacity: 1;
      transition: all 0.5s;
    }
  }
`;
