import { takeLatest, put, select } from 'redux-saga/effects';
import { FETCH_CATEGORIES, IS_LIKE_CATEGORY } from './constants';
import { fetchCategoriesSuccess } from './actions';
import * as categoriesApi from '../../api/categories';
import { Auth_NicknameSelector } from '../Auth/selectors';

export default function* categoryListSaga() {
  yield takeLatest(FETCH_CATEGORIES, fetchCategories);
}

export function* fetchCategories() {
  try {
    const categories = yield categoriesApi.get();
    yield put(fetchCategoriesSuccess(categories));
  } catch (error) {
    console.error(error);
  }
}

export function* setLikeCategory() {
  console.log('SAGA');
  yield takeLatest(IS_LIKE_CATEGORY, pathLikeCategory);
}

export function* pathLikeCategory(actions) {
  const nicknameSelector = yield select(Auth_NicknameSelector);
  const data = { ...payload, nickname: nicknameSelector };
  console.log(data);
  try {
    yield categoriesApi.post(data);
  } catch (error) {
    console.error(error);
  }
}
